import { createContext, useState, useEffect } from "react";
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import { io } from "socket.io-client";

import Root from "./pages/root";
import AllRooms from "./pages/allRooms";
import BiddingRoom from "./pages/BiddingRoom";
import CreateEditRoom from "./pages/CreateEditRoom";
import Profile from "./pages/Profile";
import Login from "./pages/login";
import { getCurrentUser } from "./data/users";

import { API_URL } from "./data/api";

const CurrentUserContext = createContext();

function App() {
  const [currentUser, setCurrentUser] = useState(null);

  // Restore user from localStorage on mount
  useEffect(() => {
    const loggedInUser = getCurrentUser();
    if (loggedInUser) {
      setCurrentUser(loggedInUser);
    }
  }, []);

  // Connect to socket.io
  useEffect(() => {
    const socket = io(API_URL);

    socket.on('updateProduct', (productId, updatedFields) => {
      console.log('updateProduct received', productId, updatedFields);
      setBowls(bowls => bowls.map(b => 
        b.id === productId ? { ...b, ...updatedFields } : b
      ));
    });

    socket.on('addProduct', (product) => {
      console.log('addProduct received', product);
      setBowls(bowls => [...bowls, product]);
    });

    socket.on('deleteProduct', (productId) => {
      console.log('deleteProduct received', productId);
      setBowls(bowls => bowls.filter(b => b.id !== productId));
    });

    // Cleanup function to prevent duplicate listeners
    return () => {
      socket.off('updateProduct');
      socket.off('addProduct');
      socket.off('deleteProduct');
      socket.disconnect();
    };
  }, []);

  const router = createBrowserRouter([
    {
      path: "/",
      element: <Root />,
      children: [
        {
          index: true,
          element: <AllRooms />,
        },
        {
          path: "rooms/new",
          element: <CreateEditRoom />,
        },
        {
          path: "rooms/:id",
          element: <BiddingRoom />,
        },
        {
          path: "rooms/:id/edit",
          element: <CreateEditRoom />,
        },
        {
          path: "profile",
          element: <Profile />,
        },
        {
          path: "login",
          element: <Login />,
        }
      ],
    },
  ]);

  return (
    <CurrentUserContext.Provider value={{ currentUser, setCurrentUser }}>
      <RouterProvider router={router} />
    </CurrentUserContext.Provider>
  );
}

export default App;
export { CurrentUserContext };
