import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

import { getRoomsWithHostNames, updateRoom } from "../data/rooms";
import RoomCard from "../components/RoomCard";

function Home() {
  const [ rooms, setRooms ] = useState([]);
  const navigate = useNavigate();
  console.log('in Home, rooms:', rooms);

  useEffect(() => {
    const fetchRooms = async () => {
      const rooms = await getRoomsWithHostNames(null);
      console.log('in Home useEffect, rooms were fetched:', rooms);
      setRooms(rooms);
    };
    fetchRooms();

    const socket = io(API_URL);

    socket.on('updateRoom', (roomId, updatedFields) => {
      console.log('updateProduct received', productId, updatedFields);
      setBowls(bowls => bowls.map(b => 
        b.id === productId ? { ...b, ...updatedFields } : b
      ));
    });

    socket.on('addProduct', (product) => {
      console.log('addProduct received', product);
      setBowls(bowls => [...bowls, product]);
    });

    socket.on('deleteProduct', (productId) => {
      console.log('deleteProduct received', productId);
      setBowls(bowls => bowls.filter(b => b.id !== productId));
    });

    // Cleanup function to prevent duplicate listeners
    return () => {
      socket.off('updateProduct');
      socket.off('addProduct');
      socket.off('deleteProduct');
      socket.disconnect();
    };
  }, []);

  return (
    <div>
      <h1>All Rooms</h1>
      <div className="rooms-grid">
        {rooms.map((room) => (
          <RoomCard 
            key={room.id} 
            room={room} 
            onClick={() => navigate(`/rooms/${room.id}`)}
          />
        ))}
      </div>
    </div>
  );
}

export default Home;
