import { createContext, useState, useEffect } from "react";
import { createBrowserRouter, RouterProvider } from "react-router-dom";
import { io } from "socket.io-client";

import Root from "./pages/root";
import AllRooms from "./pages/allRooms";
import BiddingRoom from "./pages/BiddingRoom";
import CreateEditRoom from "./pages/CreateEditRoom";
import Profile from "./pages/Profile";
import Login from "./pages/login";
import { getCurrentUser } from "./data/users";

import { API_URL } from "./data/api";

const CurrentUserContext = createContext();
const SocketContext = createContext();

function App() {
  const [currentUser, setCurrentUser] = useState(null);
  const [socket, setSocket] = useState(null);

  // Restore user from localStorage on mount
  useEffect(() => {
    const loggedInUser = getCurrentUser();
    if (loggedInUser) {
      setCurrentUser(loggedInUser);
    }
  }, []);

  // Connect to socket.io
  useEffect(() => {
    const socketConnection = io(API_URL);

    socketConnection.on('connect', () => {
      console.log('Connected to socket server');
    });

    socketConnection.on('disconnect', () => {
      console.log('Disconnected from socket server');
    });

    // Store socket instance in state so child components can use it
    setSocket(socketConnection);

    // Cleanup function to prevent duplicate listeners
    return () => {
      socketConnection.disconnect();
    };
  }, []);

  const router = createBrowserRouter([
    {
      path: "/",
      element: <Root />,
      children: [
        {
          index: true,
          element: <AllRooms />,
        },
        {
          path: "rooms/new",
          element: <CreateEditRoom />,
        },
        {
          path: "rooms/:id",
          element: <BiddingRoom />,
        },
        {
          path: "rooms/:id/edit",
          element: <CreateEditRoom />,
        },
        {
          path: "profile",
          element: <Profile />,
        },
        {
          path: "login",
          element: <Login />,
        }
      ],
    },
  ]);

  return (
    <CurrentUserContext.Provider value={{ currentUser, setCurrentUser }}>
      <RouterProvider router={router} />
    </CurrentUserContext.Provider>
  );
}

export default App;
export { CurrentUserContext };
